def get_pixel(x, y):
    return int(eval("get_pixel(" + str(x) + "," + str(y) + ")"))


def textout48(x, y, text, color, size):
    if size == 's': # Small font
        # Capitalize text
        text = text.upper()
        
        for i in range(len(text)):
            val = ord(text[i])
            if val > 122:
                val -= 26
            ind = val - 32
            
            if platform == 'win':
                print(text[i], val, ind)         
            
            for cx in range(3):
                for cy in range(5):
                    if platform == 'win':
                        print(text[i], cx, cx+ind*3, val, ind, small[cy][cx+ind*3])
                    if small[cy][cx+ind*3] == 1:
                        # if platform == 'win':
                        #     print(text[i], cx, cx+ind*3, val, ind, small[cy][cx+ind*3])
                        # else :
                        if platform != 'win':
                            pixon(0, x + i * 4 + cx, y + cy, color)

